<?phpclass UsersModel extends BaseModel {	public function __construct() {			parent::__construct( 'users' );		}		public function get_all_users() {			return $this->select()->execute();		}	public function insert_user( array $data ) {			$this->insert( $data )->execute();		}	}class GroupsModel extends BaseModel {	public function __construct() {			parent::__construct( 'groups' );		}		public function get_all_groups() {			return $this->select()->execute();		}}?>