<?phpclass UsersModel extends BaseModel {	public function __construct() {			parent::__construct( 'users' );		}		public function user_exists( $email ) {			$data = $this->select()->where( "email='?'", $email )->execute();			if( !empty( $data ) ) {					return true;				} else {					return false;				}		}		public function get_all_users() {			return $this->select()->execute();		}		public function delete_user( $user_id ) {					$this->delete()->where( 'id=?', $user_id )->execute();		}		public function update_user( array $data, $email ) {			$this->update( $data )->where( "email='?'", $email )->execute();		}	public function insert_user( array $data ) {			$this->insert( $data )->execute();		}	}class GroupsModel extends BaseModel {	public function __construct() {			parent::__construct( 'groups' );		}		public function get_group_by( $field, $value ) {			if( is_string( $value ) ) {			return $this->select()->where( "?='?'", $field, $value )->execute();		} else {			return $this->select()->where( "?=?", $field, $value )->execute();		}		}		public function get_all_groups() {			return $this->select()->execute();		}		public function delete_group( $group_id ) {			$this->delete()->where( 'id=?', $group_id )->execute();		}		public function update_group( array $data, $id ) {			$this->update( $data )->where( 'id=?', $id )->execute();		}		public function insert_group( array $data ) {			$this->insert( $data )->execute();		}}class AssignmentsModel extends BaseModel {	public function __construct() {			parent::__construct( 'assignments' );		}		public function get_all_assignments() {			return $this->select()->execute();		}}class AssignmentGroupListModel extends BaseModel {	public function __construct() {			parent::__construct( 'assignment_group_list' );		}		public function insert( $gid, $aid ) {			$this->insert( array( 'gid' => $gid, 'aid' => $aid ) )->execute();		}}?>